import React, {useContext, useState} from 'react'
import { State } from '../store'

import './gameController.scss'

const GameController = () => {
  const [ username, setUsername ] = useState(null)
  const { state, dispatch } = useContext(State)

  const sendUsername = (username) => {
    state.socket.emit(state.gameState.room, 'setUsername', {username})
  }

  return (
    <div className="game-controller">
      <header>
        <h1>
          {state.gameState.name} Room: {state.gameState.room}
        </h1>
      </header>
      <main>
        <input type="text" onChange={(e) => setUsername(e.target.value)} placeholder="Username ðŸ˜¶" autoFocus></input>
        <button type="submit" onSubmit={() => sendUsername(username)}></button>
      </main>
    </div>
  )
}

export default GameController;
