import React, {useContext, useState, useEffect} from 'react'
import { State } from '../store'
import {socket} from '../App';
import './gameController.scss'
import LeavRoom from '../leave-room/leaveRoom';

const GameController = () => {
  const [ username, setUsername ] = useState(null)
  const { state, dispatch } = useContext(State)

  useEffect(() => {
    socket.emit('join', state.gameState.room, state.playerId);

    socket.on('joinedGame', (gameState, playerId) => {
      console.log('Joined1')
      dispatch({type: 'SET_PLAYER_ID', payload: playerId})
      dispatch({type: 'UPDATE_GAME_STATE', payload: gameState})
    })

    socket.on('gameUpdate', (gameState) => {
      console.log('GAME STATE UPDATED: ', gameState)
      dispatch({type: 'UPDATE_GAME_STATE', payload: gameState})
    })
  }, [])


    // Returns a function, that, as long as it continues to be invoked, will not
// be triggered. The function will be called after it stops being called for
// N milliseconds. If `immediate` is passed, trigger the function on the
// leading edge, instead of the trailing.
const debounce = (func, wait, immediate) => {
  let timeout;

  return function exportedFunction() {
    const context = this;
    const args = arguments;

    const later = () => {
      timeout = null;
      if (!immediate) func.apply(context, args);
    };

    const callNow = immediate && !timeout;

    clearTimeout(timeout);

    timeout = setTimeout(later, wait);

    if (callNow) func.apply(context, args);
  };
};
  const sendUsername = debounce((username) => {
    socket.emit('setUsername', state.gameState.room, state.playerId, username)
  }, 2000)



  return (
    <div className="game-controller">
      <header>
        <h1>
          {state.gameState.name} Room: {state.gameState.room}
        </h1>
        <LeavRoom></LeavRoom>
        {state.gameState[state.playerId] && <React.Fragment>
          <input 
            type="text" 
            onChange={(e) => sendUsername(e.target.value.substring(state.gameState[state.playerId].emoji.length + 1, e.target.value.length))} 
            value={`${state.gameState[state.playerId].emoji} ${state.gameState[state.playerId].username}`}
            placeholder="Username" 
            maxLength="20"
            autoFocus></input>
        </React.Fragment>}
      </header>
      <main>
      </main>
    </div>
  )
}

export default GameController;
