import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import useSocket from 'use-socket.io-client';


function App() {
  const [room, setRoom] = useState('');
  const [gameState, setGameState] = useState({});
  const [socket] = useSocket('http://localhost:3000', {
    autoConnect: false,
  })

  useEffect(() => {
    socket.connect()

    socket.on('gameCreated', (msg) => {
      console.log(msg)
    });

    socket.on('gameUpdate', (gameState) => {
      setGameState(gameState)
    })
  }, [])




  const connectToGame = (roomName) => {
    socket.emit('join', roomName);
  }

  const createGame = () => {
    socket.emit('createGame');
  }


  return (
    <div className="App">
      <header className="App-header">
        <pre>
          {JSON.stringify(gameState, null, "  ")}
        </pre>
        <p>
          Start Game
          <button onClick={() => createGame()}>Start a game</button>
        </p>
        <p>
          Or Join a game
          <input type="text" onChange={(e) => setRoom(e.target.value)}></input>
          <button onClick={() => connectToGame(room)}>Join game</button>
        </p>
      </header>
    </div>
  );
}

export default App;
