import React, { useEffect, useState, useContext } from 'react'
import useSocket from 'use-socket.io-client';
import { withRouter } from 'react-router-dom';
import { State } from '../store'



const StartPage = withRouter(({ history })  => {
  const [room, setRoom] = useState('');
  const [gameState, setGameState] = useState({});
  // const [socket] = useSocket('http://localhost:3000', {
  //   autoConnect: false,
  // })
  const { state, dispatch } = useContext(State)



  console.log(state.socket)
  useEffect(() => {
    state.socket.connect()

    state.socket.on('gameUpdate', (gameState) => {
      setGameState(gameState)
    })
  }, [])

  const connectToGame = (roomName) => {
    state.socket.emit('join', roomName);
  }

  const createGame = () => {
    state.socket.emit('createGame');
  }

  return (
    <div className="App">
    <header className="App-header">
      <pre>
        {JSON.stringify(gameState, null, "  ")}
      </pre>
      <p>
        Start Game <br/>
        <button onClick={() => {
          createGame();
          history.push('/gameScreen')
        }}>Start a game</button>
      </p>
      <p>
        Or Join a game <br/>
        <input type="text" onChange={(e) => setRoom(e.target.value)}></input>
        <br/>
        <button onClick={() => connectToGame(room)}>Join game</button>
      </p>
    </header>
  </div>
  );
})

export default StartPage;
