import React, { useEffect, useState, useContext } from 'react'
import useSocket from 'use-socket.io-client';
import { Link } from 'react-router-dom';
import { State } from '../store'



const StartPage = () => {
  const [room, setRoom] = useState('');
  // const [socket] = useSocket('http://localhost:3000', {
  //   autoConnect: false,
  // })
  const { state, dispatch } = useContext(State)

  useEffect(() => {

  }, [])

  const connectToGame = (roomName) => {
    state.socket.emit('join', roomName);
  }


  return (
    <div className="App">
    <header className="App-header">
      <p>
        <Link to={"/gameScreen"}>Start a Game</Link>
      </p>
      <p>
        Or Join a game <br/>
        <input type="text" onChange={(e) => setRoom(e.target.value)}></input>
        <br/>
        <button onClick={() => connectToGame(room, state)}>Join game</button>
      </p>
    </header>
  </div>
  );
}

export default StartPage;
