import React, { useEffect, useState, useContext } from 'react'
import { State } from '../store'
import GameScreen from '../game-screen/gameScreen';
import GameController from '../game-controller/gameController';



const StartPage = () => {
  const [room, setRoom] = useState('');
  const { state, dispatch } = useContext(State)

  useEffect(() => {
    state.socket.on('gameCreated', (state) => {
      console.log(state)
      dispatch({type: 'GAME_STARTED', payload: true})
      setRoom(state.room)
    })

    state.socket.on('joinedGame', (boolean) => {
      console.log('Joined1')
      dispatch({type: 'JOINED_GAME', payload: boolean})
    })

    state.socket.on('gameUpdate', (gameState) => {
      console.log(gameState)
      dispatch({type: 'UPDATE_GAME_STATE', payload: gameState})
    })

  }, [])

  const connectToGame = (roomName) => {
    state.socket.emit('join', roomName);
  }

  const createGame = () => {
    state.socket.emit('createGame');
  }

  return (
    <React.Fragment>
      {state.gameStarted || state.joinedGame ? 
        <React.Fragment>{state.gameStarted ? <GameScreen></GameScreen> : <GameController></GameController>}</React.Fragment>
      : (<div className="App">
          <header className="App-header">
            <p>
              {/* // <Link to={"/gameScreen"}>Start a Game</Link> */}
              <button onClick={() => createGame()}>Start Game</button>
            </p>
            <p>
              Or Join a game <br/>
              <input type="text" onChange={(e) => setRoom(e.target.value)}></input>
              <br/>
              <button onClick={() => connectToGame(room, state)}>Join game</button>
            </p>
          </header>
        </div>)}
    </React.Fragment>
  );
}

export default StartPage;
